method CreateFullPath(fname: seq<char>) returns (fullPath: seq<char>)
 requires !has_dangerous_pattern(fname)
 requires non_empty_string(fname)
 requires is_valid_file_name(fname)
 requires has_valid_file_length(fname)
 requires is_valid_file_extension(fname)
 {
   var directory: seq<char> := "/home/user/documents"; // predetermined directory
   var f: FileStream;
   fullPath := f.Join(directory, fname);
 }
